import React, { useState } from 'react'
import Form from 'react-bootstrap/Form';
import axios from 'axios';
import './Login.css'
function Login() {
    const [log, setLog] = useState({
        email: "",
        password: ""
    });

    const onchg = (e) => {
        console.log(log);
        setLog({ ...log, [e.target.name]: e.target.value });
    }
    const onclk = (e) => {
        console.log(log);
        axios.post("http://127.0.0.1:8080/loguser",log)
      .then((data)=>{
        console.log(data)
        alert("Logged in successfully-")
  
      })
      .catch((error)=>{
        console.log(error)
      })
    }
  return (
    <div>
        <Form>
        <div className='logincontainer'>
        <center>
            <h3>LOGIN</h3></center><br/>
        <div className='loginformgroup'>
        <Form.Label>UserName</Form.Label>&nbsp;
        <Form.Control type='text' placeholder='Enter your Username' onChange={onchg}/>
        </div>
        <br/><br/>
        <div className='loginformgroup'>
        <Form.Label>Password</Form.Label>&nbsp;
        <Form.Control type='password' placeholder='Enter your password' onChange={onchg}/>
        </div>
        <br/><br/><center>
        <button className='loginbutton' onClick={onclk}>Submit</button>
        </center>
    </div>
    </Form>
    </div>
  )
}

export default Login
