import React, { useEffect, useState } from 'react'
import axios from 'axios';
import Table from 'react-bootstrap/Table'
import './Userprofilestyle.css';


function Userlist() {
    const [data, setData] = useState([])
    
    const viewallfn=()=>{
        axios.post("http://127.0.0.1:8080/findalluser")
        .then((result)=>{
            console.log(result)
            setData(result.data.data)
        })
        .catch((error)=>{
            console.log(error)
        })
        
    }
    useEffect(()=>{
        viewallfn();
    },[])

    const viewoneuser=()=>{
        axios.post("http://127.0.0.1:8080/viewuser/:id",data)
        .then((result)=>{
            console.log(result)
            setData(result.data.data)
        })
        .catch((error)=>{
            console.log(error)
        })
    }
    const updateuser=()=>{
        axios.post("http://127.0.0.1:8080/updateuser/:id",data)
        .then((result)=>{
            console.log(result)
            setData(result.data.data)
        })
        .catch((error)=>{
            console.log(error)
        })
    }
    const deleteuser=()=>{
        axios.post("http://127.0.0.1:8080/deleteuser/:id",data)
        .then((result)=>{
            console.log(result)
            setData(result.data.data)
        })
        .catch((error)=>{
            console.log(error)
        })
    }
    
  return (
    <div>
        <div className="userlist">
        <center>    
                <h2>User List</h2><br/>
                <div>                    
                      <table border={1}>
                          <thead>
                              <tr>
                                  <th>Sl.No</th>
                                  <th>Name </th>
                                  <th>Type of Employement</th>
                                  <th>Age</th>
                                  <th>Employee ID</th>
                                  <th>Email</th>
                                  <th>Password</th>
                                  <th>Options</th>
                              </tr>
                          </thead>

                        {
                            data.length > 0 ?
                            data.map((item, index)=>(
                                <tbody key={index}>
                                    <tr>
                                        <td>{index + 1}</td>
                                        <td>{item.empname}</td>
                                        <td>{item.typeofemp}</td>
                                        <td>{item.age}</td>
                                        <td>{item.empid}</td>
                                        <td>{item.email}</td>
                                        <td>{item.password}</td>
                                        <td><button onClick={view}>View</button>&nbsp;
                                        <button>Update</button>&nbsp;
                                        <button>Delete</button></td>
                                    </tr>
                                </tbody>
                            )):<h3>No data found</h3>
                        }   
                      </table>

                </div>
            </center>
        </div>
    </div>
  )
}

export default Userlist
